// Validation URL image
const regexURL=/https?:\/\/([a-zA-Z0-9]([^ @&%$\\/()=?¿!.,:;]|\d)*[a-zA-Z0-9][.])+[a-zA-Z]{2,4}([.][a-zA-Z]{2})?\/?((?<=\/)(([^ @&$#\\/()+=?¿!,:;'&quot;^´*%|]|\d)+[/]?)*(#(?<=#)[^ @&$#\\/()+=?¿!,:;'&quot;^´*%|]*)?(\?(?<=\?)([^ @&$#\\/()+=?¿!,:;'&quot;^´*|]+[=][^ @&$#\\/()+=?¿!,:;'&quot;^´*|]+(&(?<=&)[^ @&$#\\/()+=?¿!,:;'&quot;^´*|]+[=][^ @&$#\\/()+=?¿!,:;'&quot;^´*|]+)*))?)?/

// VALIDACIONES
export default function validation (values) {

  let errors = {};

  // Validation Name
  if (!values.name || (/^[a-zA-Z ]{1,3}$/).test(values.name)) {
    errors.name = "Please enter the name of the game, must be at least 3 characters long";
  }

  // Validation Description
  if (!values.description || values.description.length > 200) {
    errors.description = "Please enter the videogame description. (Max 200 characters)";
  }

  // Validation Rating
  if (values.rating < 0 || values.rating > 5) {
    errors.rating = "Please insert a number between 0.5 and 5";
  }

  // Validation Released
  if (!values.released) {
    errors.released = "Please insert a date";
  }

  // Validation Platforms
  if (values.platforms.length === 0) {
    errors.platforms = "Please select at least one platform";
  }

  // Validation Genres
  if (!values.genres) {
    errors.genres = "Please select at least one genre";
  }

  //Valiación de Brackground_image (URL)
  if(values.background_image === "" && !regexURL.test(inputs.background_image)){
    errors.background_image = "You must enter a valid URL. Ej:'http://www.imagen.com/imagen'"
  }

  return errors
  
};
