import React from 'react';
import styles from './Card.module.css';
import { Component } from 'react';


export class Card extends Component {
    constructor(props) {
        super(props)
    }

    render() {
    return (

        <div className={styles.card}>

            <h3 className={styles.name}>{props.name?.length > 24 ? props.name.slice(0, 23) : props.name}</h3>

            <div className={styles.cardImage} style={{ backgroundImage: `url(${props.img})` }} />

            <h6>{props.genres}</h6>
            
        </div>
    )
}     
}

